"use strict";
const Sequelize = require('sequelize');
var common = require("./common.json");
var AccountSchema = {
    identifier: {
        type: Sequelize.STRING,
        primaryKey: true,
        allowNull: false,
        unique: true,
    },
    accountType: {
        type: Sequelize.ENUM('ASANA', 'GDRIVE', 'GMAIL', 'OTHER', 'HUBSPOT', 'SALESFORCE', 'ZENDESK'),
        allowNull: false,
    },
    protocol: {
        type: Sequelize.STRING,
        allowNull: false,
    },
    data: {
        type: Sequelize.JSON,
        allowNull: false,
    },
    applicationType: {
        type: Sequelize.ENUM('CRM', 'STORAGE', 'EMAIL', 'GLOBAL', 'HELPDESK'),
        allowNull: false,
    }
};
var table = "accounts";
module.exports = {
    table,
    schema: AccountSchema,
};
