"use strict";
const Sequelize = require('sequelize');
var common = require("./common.json");
var UserSchema = {
    identifier: {
        type: Sequelize.STRING,
        primaryKey: true,
        allowNull: false,
        unique: true,
    },
    entities: {
        type: Sequelize.JSON,
        allowNull: false,
    },
    accounts: {
        type: Sequelize.JSON,
        allowNull: false,
    },
};
var table = "users";
module.exports = {
    table,
    schema: UserSchema,
};
