var config = require("../../../src/singletons/config/config");
var Should = require("should");
describe("[unit] config", function () {
    it("should exist", function (done) {
        Should(config).be.an.Object();
        done();
    });
    it("should have oauth properties in it", function (done) {
        Should(config.oAuth).be.an.Object();
        Should(config.oAuth.clientId).be.null();
        Should(config.oAuth.clientSecret).be.null();
        Should(config.oAuth.accessTokenUri).be.null();
        Should(config.oAuth.authorizationUri).be.null();
        Should(config.oAuth.redirectUri).be.null();
        Should(config.oAuth.scopes).be.null();
        done();
    });
});
