"use strict";
const Sequelize = require("sequelize");
var common = require("./common.json");
var EntitySchema = {
    collectionName: {
        type: Sequelize.STRING,
        allowNull: false,
    },
    identifier: {
        type: Sequelize.STRING,
        primaryKey: true,
        allowNull: false,
        unique: true,
    },
    accountType: {
        type: Sequelize.ENUM('ASANA', 'GMAIL', 'OTHER', 'ZENDESK'),
        allowNull: false,
    },
    applicationType: {
        type: Sequelize.ENUM('CRM', 'STORAGE', 'EMAIL', 'GLOBAL'),
        allowNull: false,
    },
    mainProperty: {
        type: Sequelize.STRING,
        allowNull: false,
    },
    idProperty: {
        type: Sequelize.STRING,
        allowNull: false,
    },
};
var table = "entities";
module.exports = {
    table,
    schema: EntitySchema,
};
