"use strict";
const Context = require("../../../src/context/context");
const Should = require('should');
describe("[integration] Context Memory", function () {
    this.timeout(10000);
    before(function (done) {
        this.timeout(30000);
        Context.db.sync({}).then(() => {
            done();
        }).catch(done);
    });
    it("should be able to find tokens history", function (done) {
        Context.token.findAll({}).then((docs) => {
            Should(docs).be.an.Array();
            done();
        }).catch(done);
    });
    it("should be able to find proactivity records", function (done) {
        Context.proactivity.findAll({}).then((docs) => {
            Should(docs).be.an.Array();
            done();
        }).catch(done);
    });
    it("should be able to find probot records", function (done) {
        Context.probot.findAll({}).then((docs) => {
            Should(docs).be.an.Array();
            done();
        }).catch(done);
    });
});
