"use strict";
var LongTermMemory = require("../../../src/long-term-memory/long-term-memory");
const Should = require('should');
describe("[integration] Long-Term Memory", function () {
    this.timeout(10000);
    it("should be able to find entity", function (done) {
        const query = LongTermMemory.db.createQuery('GMAIL-EMAILS')
            .filter('subject', '=', "Bug");
        LongTermMemory.db.runQuery(query).then((results) => {
            Should(results).be.an.Array();
            done();
        }).catch(done);
    });
});
